import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-hour',
  templateUrl: './hour.component.html',
  styleUrls: ['./hour.component.scss']
})
export class HourComponent implements OnInit {

  @Input() hourInfo: any;
  showHourForecast: boolean = false;

  constructor() { }

  ngOnInit(): void {
  }

  showForecast(event: HTMLElement){
    if(event.tagName != 'DIV'){
      var parentEl = event.parentElement.parentElement;
      this.openHoursInfo(parentEl);
    }else
      this.openHoursInfo(event);

    this.showHourForecast = !this.showHourForecast;
  }

  openHoursInfo(el: HTMLElement){
    let element = el.getElementsByClassName('hour-arrow')[0] as HTMLElement;
    if(element.style.transform == 'rotate(90deg)')
      element.style.transform = 'rotate(0deg)'
    else
      element.style.transform= 'rotate(90deg)';
  }

}
<div class="hour-container">
  <div class="hour-header-container" (click)="showForecast($event.target)">
    <div class="hour-weather-icon">
      <img src='{{ "http://openweathermap.org/img/wn/" + hourInfo.weather[0].icon + "@2x.png" }}' alt="">
    </div>
    <div class="hour-weather-time">
      <span class="hour-weather-time-title">
        Time: {{hourInfo.dt_txt}}
      </span>
    </div>
    <div class="hour-weather-desc">
      <span class="hour-weather-desc-title">
        Description: {{hourInfo.weather[0].main}}
      </span>
    </div>
    <div class="hour-arrow">
      <label> 	
        &#x276f;
      </label>
    </div>
  </div>
  <div class="hour-weather-data" *ngIf="showHourForecast">
    <div class="left-weather-data">
      <div class="temperature">
        Temperature: {{hourInfo.main.temp | celsius}}&#176;C
      </div>
      <div class="humidity">
        Humidity: {{hourInfo.main.humidity}}%
      </div>
    </div>
    <div class="right-weather-data">
      <div class="pressure">
        Pressure: {{hourInfo.main.pressure}}
      </div>
      <div class="wind">
        Wind speed: {{hourInfo.wind.speed}}
      </div>
    </div>
  </div>
</div>
